const $injector = widgetContext.$scope.$injector;
const customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
const deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
const assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
const attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
const entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
const entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
const rxjs = widgetContext.rxjs;

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    const vm = instance;

    vm.addEntityFormGroup = vm.fb.group({
        entityName: ['', [vm.validators.required]],
        entityLabel: ['', [vm.validators.required]],
        attributes: vm.fb.group({
            latitude: [null],
            longitude: [null]
        })
    });

    vm.save = () => {
        vm.addEntityFormGroup.markAsPristine();
        saveEntityObservable().pipe(
            rxjs.concatMap((sensor) => {
                return getAqiCityAndSaveRelationObservable(sensor.id).pipe(
                    rxjs.concatMap((city) => {
                        return city ? saveAttributesObservable(sensor.id, city) : rxjs.of(city);
                    })
                );
            })
        ).subscribe(() => {
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
        });
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function saveEntityObservable() {
        return  getOrCreateAqiSensorsDeviceGroupObservable().pipe(
            rxjs.concatMap((aqiSensorsGroup) => {
                const formValues = vm.addEntityFormGroup.value;
                const sensor = {
                    name: formValues.entityName,
                    label: formValues.entityLabel,
                    type: 'AQI Sensor'
                };
                return deviceService.saveDevice(sensor, aqiSensorsGroup.id.id);
            })
        );
    }

    function getOrCreateAqiSensorsDeviceGroupObservable() {
        return getEntityGroupByName('Air Quality Monitoring', 'DEVICE').pipe(
            rxjs.concatMap((group) => {
                if (group) {
                    return rxjs.of(group);
                } else {
                    const aqiSensorsGroup = {
                        type: 'DEVICE',
                        name: 'Air Quality Monitoring'
                    };
                    return entityGroupService.saveEntityGroup(aqiSensorsGroup);
                }
            })
        );
    }

    function getEntityGroupByName(groupName, groupType) {
        const entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
        return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType).pipe(
            rxjs.map((data) => {
                if (data.data.length) {
                    return data.data.find((group) => group.name === groupName);
                } else {
                    return null;
                }
            })
        );
    }

    function saveAttributesObservable(entityId, city) {
        const attributes = vm.addEntityFormGroup.get('attributes').value;
        const attributesArray = [
            {key: 'latitude', value: 34.0371318},
            {key: 'longitude', value: -118.309078},
            {key: 'state', value: 'normal'},
            {key: 'inactivityTimeout', value: city.attributes.inactivityTimeout},
            {key: 'batteryLevelThreshold', value: city.attributes.batteryLevelThreshold}
        ];
        if (attributes.latitude || attributes.longitude) {
            attributesArray[0].value = attributes.latitude;
            attributesArray[1].value = attributes.longitude;
        }
        return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    }

    function getAqiCityAndSaveRelationObservable(sensorId) {
        return assetService.getUserAssets(widgetContext.pageLink(1, 0), 'AQI City').pipe(
            rxjs.concatMap((assets) => {
                const city = assets.data.length ? assets.data[0] : null;
                if (city) {
                    return attributeService.getEntityAttributes(city.id, 'SERVER_SCOPE').pipe(
                        rxjs.concatMap((attrs) => {
                            city.attributes = {};
                            for (var i = 0; i < attrs.length; i++) {
                                city.attributes[attrs[i].key] = attrs[i].value;
                            }
                            const relation = {
                                from: city.id,
                                to: sensorId,
                                type: 'Contains'
                            };
                            return entityRelationService.saveRelation(relation).pipe(
                                rxjs.map(() => {
                                    return city;
                                })
                            );
                        })
                    );
                } else {
                    return rxjs.of(city);
                }
            })
        );
    }
}

