.main-layout {
    width: 100%;
    height: 100%;
    padding: 16px 20px 0 20px;
}

.main-layout .header .entity-name {
    font-size: 18px;
    font-weight: 500;
    line-height: 24px;
    letter-spacing: 0.25px;
    align-items: center;
}

.main-layout .header .go-back-bttn {
    margin-right: 4px;
    width: 24px;
    height: 24px;
    min-width: 24px;
