settings.dashboardCss:
.tb-markdown-view .tb-progress-cover, .tb-markdown-view .mat-drawer-container {
    background-color: #fff;
}

/* Aqi Components Popup Styles */

.aqi-component-desc-popup {
    width: 100%;
    height: 100%;
    padding: 20px 24px 28px 24px;
}

.aqi-component-desc-popup .header,
.aqi-component-desc-popup .component-name-header {
    font-weight: 600;
    font-size: 22px;
    line-height: 28px;
    letter-spacing: 0.25px;
    color: #29313C;
}

.aqi-component-desc-popup .component-name-header {
    margin-bottom: 4px;
}

.aqi-component-desc-popup .header {
    margin-bottom: 16px;
}

.aqi-component-desc-popup .entity-name-header {
    margin-bottom: 24px;
    font-weight: 500;
    font-size: 18px;
    line-height: 24px;
    letter-spacing: 0.25px;
    color: #9FA6B4;
}

.aqi-component-desc-popup .history-container {
    height: 65%;
    width: 100%;
    margin-bottom: 40px;
}

.aqi-component-desc-popup .history-container .history-tabs .mat-tab-header {
    border-bottom: none;
}

.aqi-component-desc-popup .history-container .history-tabs .mat-tab-label {
    height: 40px;
    min-width: 50px;
    opacity: 1;
}

.aqi-component-desc-popup .history-container .history-tabs .mat-tab-label .mat-tab-label-content {
    font-size: 14px;
    font-weight: 600;
    line-height: 20px;
    color: #9FA6B4;
}

.aqi-component-desc-popup .history-container .history-tabs .mat-tab-label.mat-tab-label-active .mat-tab-label-content {
    color: #29313C;
}

.aqi-component-desc-popup .history-container .history-tabs .mat-ink-bar {
    background-color: #4B535B;
}

.aqi-component-desc-popup .history-container .history-tabs {
    height: 100%;
}

.aqi-component-desc-popup .history-container .history-tabs .mat-tab-body-wrapper {
    height: calc(100% - 40px);
}

.aqi-component-desc-popup .component-values-container {
    margin-bottom: 24px;
}

.aqi-component-desc-popup .component-values-container > div:not(:last-child) {
    margin-right: 24px;
}

.aqi-component-desc-popup .component-value-header {
    margin-bottom: 8px;
    font-weight: 500;
    font-size: 14px;
    line-height: 18px;
    letter-spacing: 0.25px;
    color: #9FA6B4;
}

.aqi-component-desc-popup .component-value-row {
    display: flex;
    flex-direction: row;
    font-size: 14px;
}

.aqi-component-desc-popup .component-value {
    margin-right: 4px;
    line-height: 20px;
    font-weight: 600;
    color: #29313C;
}

.aqi-component-desc-popup .component-unit {
    font-weight: 400;
    line-height: 20px;
    letter-spacing: 0.25px;
    color: #29313C;
}

.aqi-component-desc-popup .component-desc-text {
    font-weight: 400;
    font-size: 14px;
    line-height: 20px;
    letter-spacing: 0.25px;
    color: #29313C;
}
