settings.markdownTextFunction:
var content;
if (data.length) {
    data.sort(function (data1, data2) {
       return data1.entityName.localeCompare(data2.entityName); 
    });
    var tabs = '';
    for (let i = 0; i< data.length; i++) {
        tabs += createTab(data[i]);
    }
    content = '<mat-tab-group fxFlex class="zone-tabs">' + tabs + '</mat-tab-group>';
} else {
    content = '<div fxFlex style="width: 100%;" fxLayout="column" fxLayoutAlign="center center">No zones configured</div>';
}

return '<section style="width: 100%; height: 100%;" fxLayout="column" fxLayoutAlign="start start">' +
          content +
          '<button id="legend" mat-button>Legend</button>' +
        '</section>';

function createTab(entityData) {
    return '<mat-tab label="' + entityData.entityName + '">' +
             '<tb-dashboard-state style="width: 100%; height: 100%;" [ctx]="ctx" stateId="zone" entityParamName="zone" [entityId]="{entityType: \'ASSET\', id: \''+ entityData.entityId +'\'}"></tb-dashboard-state>' +
            '</mat-tab>';
}

