actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditZoneDialog();

function openEditZoneDialog() {
    customDialog.customDialog(htmlTemplate, EditZoneDialogController).subscribe();
}

function EditZoneDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entityId = entityId;

    vm.widgetContext = widgetContext;
    vm.zone = null;

    vm.editZoneFormGroup = vm.fb.group({
      name: ['', [vm.validators.required]],
      plan: [null, [vm.validators.required]]
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editZoneFormGroup.markAsPristine();
        widgetContext.rxjs.forkJoin([
            saveAttributes(entityId),
            saveZone()
        ]).subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    loadZone();

    function saveZone() {
        const formValues = vm.editZoneFormGroup.value;
        vm.zone.name = formValues.name;
        return assetService.saveAsset(vm.zone);
    }

    function saveAttributes(entityId) {
        let plan = vm.editZoneFormGroup.get('plan').value;
        let attributesArray = [
            {key: 'plan', value: plan}
        ];
        return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    }

    function loadZone() {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['plan']),
            assetService.getAsset(entityId.id)
        ]).subscribe(
            function (data) {
                var attributes = data[0];
                for (var i = 0; i < attributes.length; i ++) {
                    if (attributes[i].key === 'plan') {
                        var plan = attributes[i].value;
                        vm.editZoneFormGroup.get('plan').patchValue(plan, {emitEvent: false});
                    }
                }
                vm.zone = data[1];
                vm.editZoneFormGroup.get('name').patchValue(vm.zone.name, {emitEvent: false});
            }
        );
    }

}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = 'Delete zone \'' +
                 entityName + '\'';
    let content = 'Are you sure you want to delete zone \'' + entityName + '\'?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                deleteEntity();
            }
        }
    );
    $('tb-confirm-dialog').addClass('al-delete-dialog');
}

function deleteEntity() {
    deleteEntityObservable(entityId).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function deleteEntityObservable(entityId) {
    return assetService.deleteAsset(entityId.id);
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting zone. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

