elementClick.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let userService = $injector.get(widgetContext.servicesMap.get('userService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

openAddResidentDialog();

function openAddResidentDialog() {
    customDialog.customDialog(htmlTemplate, AddResidentDialogController).subscribe();
}

function AddResidentDialogController(instance) {
    let vm = instance;

    vm.customerId = null;

    const phoneNumberPattern = /^\+[1-9]\d{1,14}$/;

    vm.widgetContext = widgetContext;

    vm.addResidentFormGroup = vm.fb.group({
      email: ['', [vm.validators.required, vm.validators.email]],
      firstName: ['', [vm.validators.required]],
      lastName: ['', [vm.validators.required]],
      gender: [null],
      phoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]],
      birthdate: [null],
      photo: [null, [vm.validators.required]],
      emergencyFirstname: [''],
      emergencyLastname: [''],
      emergencyRole: [''],
      emergencyPhoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]],
      chronicDiseases: [''],
      allergy: [''],
      zone: [null, [vm.validators.required]],
      room: [null, [vm.validators.required]],
      wristband: [null, [vm.validators.required]]
    });

    vm.genders = ['Male', 'Female'];

    vm.zones$ = getCustomerId().pipe(
        widgetContext.rxjs.tap((customerId) => {
            vm.customerId = customerId;
        }),
        widgetContext.rxjs.switchMap((ownerId) => {
            var query = {
                parameters: {
                    rootId: ownerId.id,
                    rootType: ownerId.entityType,
                    direction: 'FROM',
                    maxLevel: 1
                },
                relationType: 'Contains',
                assetTypes: ['AL_Zone']
            };
            return assetService.findByQuery(query).pipe(
                widgetContext.rxjs.map((zones) => {
                    return zones.sort((z1, z2) => z1.name.localeCompare(z2.name));
                })
            );
        }),
        widgetContext.rxjs.share()
    );

    vm.rooms$ = vm.addResidentFormGroup.get('zone').valueChanges.pipe(
        widgetContext.rxjs.tap(() => {
            vm.addResidentFormGroup.get('room').patchValue(null);
        }),
        widgetContext.rxjs.switchMap((zone) => {
            if (zone) {
                var query = {
                    parameters: {
                        rootId: zone.id.id,
                        rootType: zone.id.entityType,
                        direction: 'FROM',
                        maxLevel: 1
                    },
                    relationType: 'Contains',
                    assetTypes: ['AL_Room']
                };
                return assetService.findByQuery(query).pipe(
                    widgetContext.rxjs.map((rooms) => {
                        return rooms.sort((r1, r2) => r1.name.localeCompare(r2.name));
                    })
                );
            } else {
                return widgetContext.rxjs.of([]);
            }
        }),
        widgetContext.rxjs.share()
    );

    vm.wristbands$ = getCustomerId().pipe(
        widgetContext.rxjs.switchMap((ownerId) => {
           return getAvailableWristbands(ownerId);
        }),
        widgetContext.rxjs.share()
    );

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.add = function() {
        vm.addResidentFormGroup.markAsPristine();
        saveEntityObservable(vm.customerId).subscribe(
            function (entity) {
                widgetContext.rxjs.forkJoin([
                    saveRelations(vm.customerId, entity),
                    saveAttributes(entity.id)
                ]).subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );
            }
        );
    };


    function saveEntityObservable(ownerId) {
        return getOrCreateResidentsUserGroup(ownerId).pipe(
            widgetContext.rxjs.switchMap((residents) => {
                const formValues = vm.addResidentFormGroup.value;
                let resident = {
                    email: formValues.email,
                    firstName: formValues.firstName,
                    lastName: formValues.lastName,
                    customerId: ownerId,
                    authority: 'CUSTOMER_USER'
                };
                return userService.saveUser(resident, false, residents.id.id);
            })
        );
    }

    function saveRelations(ownerId, entity) {
        let ownerRelation = {
            type: 'Resident',
            typeGroup: 'COMMON',
            from: ownerId,
            to: entity.id
        };
        let room = vm.addResidentFormGroup.get('room').value;
        let roomRelation = {
            type: 'Contains',
            typeGroup: 'COMMON',
            from: room.id,
            to: entity.id
        };
        let wristband = vm.addResidentFormGroup.get('wristband').value;
        return widgetContext.rxjs.forkJoin(
            [
                entityRelationService.saveRelation(ownerRelation),
                entityRelationService.saveRelation(roomRelation),
                assignWristband(entity, wristband.id)
            ]
        );
    }

    function saveAttributes(entityId) {
        let room = vm.addResidentFormGroup.get('room').value;
        return attributeService.getEntityAttributes(room.id, 'SERVER_SCOPE', ['xPos', 'yPos']).pipe(
            widgetContext.rxjs.switchMap((attributes) => {
                let xPos = 0;
                let yPos = 0;
                for (var i = 0; i < attributes.length; i ++) {
                    if (attributes[i].key === 'xPos') {
                        xPos = attributes[i].value;
                    } else if (attributes[i].key === 'yPos') {
                        yPos = attributes[i].value;
                    }
                }
                let photo = vm.addResidentFormGroup.get('photo').value;
                let zone = vm.addResidentFormGroup.get('zone').value;
                let gender = vm.addResidentFormGroup.get('gender').value;
                let phoneNumber = vm.addResidentFormGroup.get('phoneNumber').value;
                let birthdate = vm.addResidentFormGroup.get('birthdate').value;
                if (birthdate) {
                    birthdate = birthdate.getTime();
                } else {
                    birthdate = null;
                }
                let emergencyFirstname = vm.addResidentFormGroup.get('emergencyFirstname').value;
                let emergencyLastname = vm.addResidentFormGroup.get('emergencyLastname').value;
                let emergencyRole = vm.addResidentFormGroup.get('emergencyRole').value;
                let emergencyPhoneNumber = vm.addResidentFormGroup.get('emergencyPhoneNumber').value;
                let chronicDiseases = vm.addResidentFormGroup.get('chronicDiseases').value;
                let allergy = vm.addResidentFormGroup.get('allergy').value;
                let attributesArray = [
                    {key: 'photo', value: photo},
                    {key: 'gender', value: gender},
                    {key: 'phoneNumber', value: phoneNumber},
                    {key: 'birthdate', value: birthdate},
                    {key: 'emergencyFirstname', value: emergencyFirstname},
                    {key: 'emergencyLastname', value: emergencyLastname},
                    {key: 'emergencyRole', value: emergencyRole},
                    {key: 'emergencyPhoneNumber', value: emergencyPhoneNumber},
                    {key: 'chronicDiseases', value: chronicDiseases},
                    {key: 'allergy', value: allergy},
                    {key: 'zone', value: zone.name},
                    {key: 'room', value: room.name},
                    {key: 'currentZone', value: zone.name},
                    {key: 'currentRoom', value: room.name},
                    {key: 'xPos', value: xPos},
                    {key: 'yPos', value: yPos}
                ];
                return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
            })
        );
    }

    function assignWristband(entity, wristbandId) {
        let newWristbandRelation = {
            type: 'Use',
            typeGroup: 'COMMON',
            from: entity.id,
            to: wristbandId
        };
        return deviceService.getDevice(wristbandId.id).pipe(
            widgetContext.rxjs.switchMap((wristband) => {
                return getAssignedUnassignedDeviceGroupIds().pipe(
                    widgetContext.rxjs.switchMap((groupIds) => {
                        wristband.label = entity.firstName + ' ' + entity.lastName;
                        return widgetContext.rxjs.forkJoin([
                            deviceService.saveDevice(wristband),
                            entityRelationService.saveRelation(newWristbandRelation),
                            entityGroupService.removeEntityFromEntityGroup(groupIds.unassignedGroupId, wristbandId.id),
                            entityGroupService.addEntityToEntityGroup(groupIds.assignedGroupId, wristbandId.id)
                        ]);
                    })
                );
            })
        );

    }

    function getAssignedUnassignedDeviceGroupIds() {
        return widgetContext.rxjs.forkJoin([
            getOrCreateEntityGroup(vm.customerId, 'DEVICE', 'Unassigned Devices'),
            getOrCreateEntityGroup(vm.customerId, 'DEVICE', 'Assigned Devices')
        ]).pipe(
            widgetContext.rxjs.map((groups) => {
              return { unassignedGroupId: groups[0].id.id, assignedGroupId: groups[1].id.id };
            })
        );
    }

    function getOrCreateResidentsUserGroup(ownerId) {
        return getOrCreateEntityGroup(ownerId, "USER", "Residents");
    }

    function getAvailableWristbands(ownerId) {
        const query = {
            pageLink: {
                pageSize: 100,
                page: 0
            },
            entityFields: [
                {
                    type: 'ENTITY_FIELD',
                    key: 'name'
                }
            ],
            entityFilter: {
                type: 'entitiesByGroupName',
                groupType: 'DEVICE',
                ownerId: ownerId,
                entityGroupNameFilter: 'Unassigned Devices'
            },
            keyFilters: [
                {
                    key: {
                        type: 'ENTITY_FIELD',
                        key: 'type'
                    },
                    valueType: 'STRING',
                    predicate: {
                        type: 'STRING',
                        operation: 'EQUAL',
                        value: { defaultValue: 'AL_Wristband'},
                        ignoreCase: false
                    }
                }
            ]
        };
        return entityService.findEntityDataByQuery(query).pipe(
            widgetContext.rxjs.map((pageData) => {
                const wristbands = convertEntityPageDataToNameIdPairs(pageData);
                wristbands.sort((w1, w2) => w1.name.localeCompare(w2.name));
                return wristbands;
            })
        );
    }

    function convertEntityPageDataToNameIdPairs(pageData) {
        return pageData.data.map((entityData) => {
            return convertEntityDataToNameIdPair(entityData);
        });
    }

    function convertEntityDataToNameIdPair(entityData) {
        return {
            id: entityData.entityId,
            name: entityData.latest['ENTITY_FIELD']['name'].value
        };
    }

    function getOrCreateEntityGroup(ownerId, type, name) {
      return getEntityGroupByName(ownerId, name, type).pipe(
          widgetContext.rxjs.switchMap((group) => {
              if (group) {
                  return widgetContext.rxjs.of(group);
              } else {
                  var newGroup = {
                    type: type,
                    name: name,
                    ownerId: ownerId
                  };
                  return entityGroupService.saveEntityGroup(newGroup);
              }
          })
      );
    }

    function getEntityGroupByName(ownerId, groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByOwnerIdAndPageLink(ownerId.entityType, ownerId.id, groupType, entityGroupsPageLink, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
    }

    function getCustomerId() {
        const customerAliasId = widgetContext.aliasController.getEntityAliasId('Customer');
        return widgetContext.aliasController.getAliasInfo(customerAliasId).pipe(
            widgetContext.rxjs.map((info) => {
              return {id: info.currentEntity.id, entityType: info.currentEntity.entityType};  
            })
        );
    }

}

settings.cellContentFunction:
if (value !== null && value !== '') {
    let birthday = new Date(value);
    var ageDifMs = Date.now() - birthday.getTime();
    var ageDate = new Date(ageDifMs);
    return Math.abs(ageDate.getUTCFullYear() - 1970);
} else {
    return '';
}

