headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openRulesDialog();

function openRulesDialog() {
    customDialog.customDialog(htmlTemplate, RulesDialogController).subscribe();
}

function RulesDialogController(instance) {
    const vm = instance;

    const phoneNumberPattern = /^\+[1-9]\d{1,14}$/;

    vm.widgetContext = widgetContext;

    vm.rulesFormGroup = vm.fb.group({
      panicButtonAlarmEnabled: [null],
      majorPanicButtonThreshold: [null, [vm.validators.required, vm.validators.min(1), vm.validators.max(10)]],
      criticalPanicButtonThreshold: [null, [vm.validators.required, vm.validators.min(1), vm.validators.max(10)]],
      heartRateAlarmEnabled: [null],
      minMajorHeartRateThreshold: [null, [vm.validators.required, vm.validators.min(1), vm.validators.max(200)]],
      maxMajorHeartRateThreshold: [null, [vm.validators.required, vm.validators.min(1), vm.validators.max(200)]],
      minCriticalHeartRateThreshold: [null, [vm.validators.required, vm.validators.min(1), vm.validators.max(200)]],
      maxCriticalHeartRateThreshold: [null, [vm.validators.required, vm.validators.min(1), vm.validators.max(200)]],
      bodyTemperatureAlarmEnabled: [null],
      minMajorBodyTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(30), vm.validators.max(50)]],
      maxMajorBodyTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(30), vm.validators.max(50)]],
      minCriticalBodyTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(30), vm.validators.max(50)]],
      maxCriticalBodyTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(30), vm.validators.max(50)]],
      noiseLevelAlarmEnabled: [null],
      noiseLevelMajorThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(200)]],
      noiseLevelCriticalThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(200)]],
      emergencyPhoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]],
      nursePhoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]]
    });

    updateDisabledState();

    for (const key of Object.keys(vm.rulesFormGroup.controls)) {
        vm.rulesFormGroup.get(key).valueChanges.subscribe((newValue) => {
            if (vm.rulesFormGroup.get(key).valid) {
                getCustomerId().pipe(
                    widgetContext.rxjs.switchMap((customerId) => {
                        return attributeService.saveEntityAttributes(customerId, 'SERVER_SCOPE', [{key: key, value: newValue}]);
                    })
                ).subscribe(() => {
                    if (['panicButtonAlarmEnabled', 'heartRateAlarmEnabled', 'bodyTemperatureAlarmEnabled', 'noiseLevelAlarmEnabled'].includes(key)) {
                        updateDisabledState();
                    }
                });
            }
        });
    }

    function updateDisabledState() {
        let panicButtonAlarmEnabled = vm.rulesFormGroup.get('panicButtonAlarmEnabled').value;
        let heartRateAlarmEnabled = vm.rulesFormGroup.get('heartRateAlarmEnabled').value;
        let bodyTemperatureAlarmEnabled = vm.rulesFormGroup.get('bodyTemperatureAlarmEnabled').value;
        let noiseLevelAlarmEnabled = vm.rulesFormGroup.get('noiseLevelAlarmEnabled').value;
        if (panicButtonAlarmEnabled) {
            vm.rulesFormGroup.get('majorPanicButtonThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('criticalPanicButtonThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('majorPanicButtonThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('criticalPanicButtonThreshold').disable({emitEvent: false});
        }
        if (heartRateAlarmEnabled) {
            vm.rulesFormGroup.get('minMajorHeartRateThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorHeartRateThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalHeartRateThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalHeartRateThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('minMajorHeartRateThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorHeartRateThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalHeartRateThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalHeartRateThreshold').disable({emitEvent: false});
        }
        if (bodyTemperatureAlarmEnabled) {
            vm.rulesFormGroup.get('minMajorBodyTemperatureThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorBodyTemperatureThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalBodyTemperatureThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalBodyTemperatureThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('minMajorBodyTemperatureThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorBodyTemperatureThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalBodyTemperatureThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalBodyTemperatureThreshold').disable({emitEvent: false});
        }
        if (noiseLevelAlarmEnabled) {
            vm.rulesFormGroup.get('noiseLevelMajorThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('noiseLevelCriticalThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('noiseLevelMajorThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('noiseLevelCriticalThreshold').disable({emitEvent: false});
        }
    }

    vm.close = function() {
        vm.dialogRef.close(null);
    };

    loadRules();

    function loadRules() {
        loadCustomerResidentAlarmsAttributes().subscribe(
            function (attributes) {
                vm.rulesFormGroup.get('panicButtonAlarmEnabled').patchValue(getAttributeValue(attributes, 'panicButtonAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('majorPanicButtonThreshold').patchValue(getAttributeValue(attributes, 'majorPanicButtonThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('criticalPanicButtonThreshold').patchValue(getAttributeValue(attributes, 'criticalPanicButtonThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('heartRateAlarmEnabled').patchValue(getAttributeValue(attributes, 'heartRateAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('minMajorHeartRateThreshold').patchValue(getAttributeValue(attributes, 'minMajorHeartRateThreshold', null), {emitEvent: false});                
                vm.rulesFormGroup.get('maxMajorHeartRateThreshold').patchValue(getAttributeValue(attributes, 'maxMajorHeartRateThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('minCriticalHeartRateThreshold').patchValue(getAttributeValue(attributes, 'minCriticalHeartRateThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalHeartRateThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalHeartRateThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('bodyTemperatureAlarmEnabled').patchValue(getAttributeValue(attributes, 'bodyTemperatureAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('minMajorBodyTemperatureThreshold').patchValue(getAttributeValue(attributes, 'minMajorBodyTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxMajorBodyTemperatureThreshold').patchValue(getAttributeValue(attributes, 'maxMajorBodyTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('minCriticalBodyTemperatureThreshold').patchValue(getAttributeValue(attributes, 'minCriticalBodyTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalBodyTemperatureThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalBodyTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('noiseLevelAlarmEnabled').patchValue(getAttributeValue(attributes, 'noiseLevelAlarmEnabled', false), {emitEvent: false});  
                vm.rulesFormGroup.get('noiseLevelMajorThreshold').patchValue(getAttributeValue(attributes, 'noiseLevelMajorThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('noiseLevelCriticalThreshold').patchValue(getAttributeValue(attributes, 'noiseLevelCriticalThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('emergencyPhoneNumber').patchValue(getAttributeValue(attributes, 'emergencyPhoneNumber', null), {emitEvent: false});
                vm.rulesFormGroup.get('nursePhoneNumber').patchValue(getAttributeValue(attributes, 'nursePhoneNumber', null), {emitEvent: false});
                updateDisabledState();
            }
        );
    }

    function getAttributeValue(attributes, key, defaultValue) {
        let value = null;
        let found = attributes.filter((attribute) => attribute.key === key);
        if (found.length) {
            value = found[0].value;
        }
        if (value === null || value === '') {
            value = defaultValue;
        }
        return value;
    }

    function loadCustomerResidentAlarmsAttributes() {
        return getCustomerId().pipe(
            widgetContext.rxjs.switchMap((customerId) => {
                return attributeService.getEntityAttributes(customerId, 'SERVER_SCOPE', 
                    ['panicButtonAlarmEnabled', 
                     'majorPanicButtonThreshold', 
                     'criticalPanicButtonThreshold', 
                     'heartRateAlarmEnabled', 
                     'minMajorHeartRateThreshold',
                     'maxMajorHeartRateThreshold',
                     'minCriticalHeartRateThreshold',
                     'maxCriticalHeartRateThreshold',
                     'bodyTemperatureAlarmEnabled',
                     'minMajorBodyTemperatureThreshold',
                     'maxMajorBodyTemperatureThreshold',
                     'minCriticalBodyTemperatureThreshold',
                     'maxCriticalBodyTemperatureThreshold',
                     'noiseLevelAlarmEnabled',
                     'noiseLevelMajorThreshold',
                     'noiseLevelCriticalThreshold',
                     'emergencyPhoneNumber',
                     'nursePhoneNumber']
                );
            })
        );
    }

    function getCustomerId() {
        const customerAliasId = widgetContext.aliasController.getEntityAliasId('Customer');
        return widgetContext.aliasController.getAliasInfo(customerAliasId).pipe(
            widgetContext.rxjs.map((info) => {
              return {id: info.currentEntity.id, entityType: info.currentEntity.entityType};  
            })
        );
    }

}

