headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openRulesDialog();

function openRulesDialog() {
    customDialog.customDialog(htmlTemplate, RulesDialogController).subscribe();
}

function RulesDialogController(instance) {
    const vm = instance;

    const phoneNumberPattern = /^\+[1-9]\d{1,14}$/;

    vm.widgetContext = widgetContext;

    vm.rulesFormGroup = vm.fb.group({
      roomTemperatureAlarmEnabled: [null],
      minMajorRoomTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(50)]],
      maxMajorRoomTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(50)]],
      minCriticalRoomTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(50)]],
      maxCriticalRoomTemperatureThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(50)]],

      roomHumidityAlarmEnabled: [null],
      minMajorRoomHumidityThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],
      maxMajorRoomHumidityThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],
      minCriticalRoomHumidityThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],
      maxCriticalRoomHumidityThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],

      roomIaqAlarmEnabled: [null],
      maxMajorRoomIaqThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(500)]],
      maxCriticalRoomIaqThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(500)]],

      doorOpenAlarmEnabled: [null],
      maxMajorDoorOpenThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(1000)]],
      maxCriticalDoorOpenThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(1000)]],

      windowOpenAlarmEnabled: [null],
      maxMajorWindowOpenThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(1000)]],
      maxCriticalWindowOpenThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(1000)]],

      batteryLevelAlarmEnabled: [null],
      minMajorBatteryLevelThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],
      minCriticalBatteryLevelThreshold: [null, [vm.validators.required, vm.validators.min(0), vm.validators.max(100)]],

      waterLeakAlarmEnabled: [null],
      smokeAlarmEnabled: [null],
      attendantPhoneNumber: [null, [vm.validators.pattern(phoneNumberPattern)]]
    });

    updateDisabledState();

    for (const key of Object.keys(vm.rulesFormGroup.controls)) {
        vm.rulesFormGroup.get(key).valueChanges.subscribe((newValue) => {
            if (vm.rulesFormGroup.get(key).valid) {
                getCustomerId().pipe(
                    widgetContext.rxjs.switchMap((customerId) => {
                        return attributeService.saveEntityAttributes(customerId, 'SERVER_SCOPE', [{key: key, value: newValue}]);
                    })
                ).subscribe(() => {
                    if (['roomTemperatureAlarmEnabled', 'roomHumidityAlarmEnabled', 'roomIaqAlarmEnabled',
                         'doorOpenAlarmEnabled', 'windowOpenAlarmEnabled', 'batteryLevelAlarmEnabled'].includes(key)) {
                        updateDisabledState();
                    }
                });
            }
        });
    }

    function updateDisabledState() {
        let roomTemperatureAlarmEnabled = vm.rulesFormGroup.get('roomTemperatureAlarmEnabled').value;
        let roomHumidityAlarmEnabled = vm.rulesFormGroup.get('roomHumidityAlarmEnabled').value;
        let roomIaqAlarmEnabled = vm.rulesFormGroup.get('roomIaqAlarmEnabled').value;
        let doorOpenAlarmEnabled = vm.rulesFormGroup.get('doorOpenAlarmEnabled').value;
        let windowOpenAlarmEnabled = vm.rulesFormGroup.get('windowOpenAlarmEnabled').value;
        let batteryLevelAlarmEnabled = vm.rulesFormGroup.get('batteryLevelAlarmEnabled').value;

        if (roomTemperatureAlarmEnabled) {
            vm.rulesFormGroup.get('minMajorRoomTemperatureThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorRoomTemperatureThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalRoomTemperatureThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalRoomTemperatureThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('minMajorRoomTemperatureThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorRoomTemperatureThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalRoomTemperatureThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalRoomTemperatureThreshold').disable({emitEvent: false});
        }

        if (roomHumidityAlarmEnabled) {
            vm.rulesFormGroup.get('minMajorRoomHumidityThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorRoomHumidityThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalRoomHumidityThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalRoomHumidityThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('minMajorRoomHumidityThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxMajorRoomHumidityThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalRoomHumidityThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalRoomHumidityThreshold').disable({emitEvent: false});
        }

        if (roomIaqAlarmEnabled) {
            vm.rulesFormGroup.get('maxMajorRoomIaqThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalRoomIaqThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('maxMajorRoomIaqThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalRoomIaqThreshold').disable({emitEvent: false});
        }

        if (doorOpenAlarmEnabled) {
            vm.rulesFormGroup.get('maxMajorDoorOpenThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalDoorOpenThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('maxMajorDoorOpenThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalDoorOpenThreshold').disable({emitEvent: false});
        }

        if (windowOpenAlarmEnabled) {
            vm.rulesFormGroup.get('maxMajorWindowOpenThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalWindowOpenThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('maxMajorWindowOpenThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('maxCriticalWindowOpenThreshold').disable({emitEvent: false});
        }

        if (batteryLevelAlarmEnabled) {
            vm.rulesFormGroup.get('minMajorBatteryLevelThreshold').enable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalBatteryLevelThreshold').enable({emitEvent: false});
        } else {
            vm.rulesFormGroup.get('minMajorBatteryLevelThreshold').disable({emitEvent: false});
            vm.rulesFormGroup.get('minCriticalBatteryLevelThreshold').disable({emitEvent: false});
        }
    }

    vm.close = function() {
        vm.dialogRef.close(null);
    };

    loadRules();

    function loadRules() {
        loadCustomerResidentAlarmsAttributes().subscribe(
            function (attributes) {
                vm.rulesFormGroup.get('roomTemperatureAlarmEnabled').patchValue(getAttributeValue(attributes, 'roomTemperatureAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('minMajorRoomTemperatureThreshold').patchValue(getAttributeValue(attributes, 'minMajorRoomTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxMajorRoomTemperatureThreshold').patchValue(getAttributeValue(attributes, 'maxMajorRoomTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('minCriticalRoomTemperatureThreshold').patchValue(getAttributeValue(attributes, 'minCriticalRoomTemperatureThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalRoomTemperatureThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalRoomTemperatureThreshold', null), {emitEvent: false});

                vm.rulesFormGroup.get('roomHumidityAlarmEnabled').patchValue(getAttributeValue(attributes, 'roomHumidityAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('minMajorRoomHumidityThreshold').patchValue(getAttributeValue(attributes, 'minMajorRoomHumidityThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxMajorRoomHumidityThreshold').patchValue(getAttributeValue(attributes, 'maxMajorRoomHumidityThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('minCriticalRoomHumidityThreshold').patchValue(getAttributeValue(attributes, 'minCriticalRoomHumidityThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalRoomHumidityThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalRoomHumidityThreshold', null), {emitEvent: false});

                vm.rulesFormGroup.get('roomIaqAlarmEnabled').patchValue(getAttributeValue(attributes, 'roomIaqAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('maxMajorRoomIaqThreshold').patchValue(getAttributeValue(attributes, 'maxMajorRoomIaqThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalRoomIaqThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalRoomIaqThreshold', null), {emitEvent: false});

                vm.rulesFormGroup.get('doorOpenAlarmEnabled').patchValue(getAttributeValue(attributes, 'doorOpenAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('maxMajorDoorOpenThreshold').patchValue(getAttributeValue(attributes, 'maxMajorDoorOpenThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalDoorOpenThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalDoorOpenThreshold', null), {emitEvent: false});

                vm.rulesFormGroup.get('windowOpenAlarmEnabled').patchValue(getAttributeValue(attributes, 'windowOpenAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('maxMajorWindowOpenThreshold').patchValue(getAttributeValue(attributes, 'maxMajorWindowOpenThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('maxCriticalWindowOpenThreshold').patchValue(getAttributeValue(attributes, 'maxCriticalWindowOpenThreshold', null), {emitEvent: false});

                vm.rulesFormGroup.get('batteryLevelAlarmEnabled').patchValue(getAttributeValue(attributes, 'batteryLevelAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('minMajorBatteryLevelThreshold').patchValue(getAttributeValue(attributes, 'minMajorBatteryLevelThreshold', null), {emitEvent: false});
                vm.rulesFormGroup.get('minCriticalBatteryLevelThreshold').patchValue(getAttributeValue(attributes, 'minCriticalBatteryLevelThreshold', null), {emitEvent: false});

                vm.rulesFormGroup.get('waterLeakAlarmEnabled').patchValue(getAttributeValue(attributes, 'waterLeakAlarmEnabled', false), {emitEvent: false});
                vm.rulesFormGroup.get('smokeAlarmEnabled').patchValue(getAttributeValue(attributes, 'smokeAlarmEnabled', false), {emitEvent: false});

                vm.rulesFormGroup.get('attendantPhoneNumber').patchValue(getAttributeValue(attributes, 'attendantPhoneNumber', null), {emitEvent: false});
                updateDisabledState();
            }
        );
    }

    function getAttributeValue(attributes, key, defaultValue) {
        let value = null;
        let found = attributes.filter((attribute) => attribute.key === key);
        if (found.length) {
            value = found[0].value;
        }
        if (value === null || value === '') {
            value = defaultValue;
        }
        return value;
    }

    function loadCustomerResidentAlarmsAttributes() {
        return getCustomerId().pipe(
            widgetContext.rxjs.switchMap((customerId) => {
                return attributeService.getEntityAttributes(customerId, 'SERVER_SCOPE', 
                    ['roomTemperatureAlarmEnabled', 
                     'minMajorRoomTemperatureThreshold', 
                     'maxMajorRoomTemperatureThreshold', 
                     'minCriticalRoomTemperatureThreshold', 
                     'maxCriticalRoomTemperatureThreshold',

                     'roomHumidityAlarmEnabled',
                     'minMajorRoomHumidityThreshold',
                     'maxMajorRoomHumidityThreshold',
                     'minCriticalRoomHumidityThreshold',
                     'maxCriticalRoomHumidityThreshold',

                     'roomIaqAlarmEnabled',
                     'maxMajorRoomIaqThreshold',
                     'maxCriticalRoomIaqThreshold',

                     'doorOpenAlarmEnabled',
                     'maxMajorDoorOpenThreshold',
                     'maxCriticalDoorOpenThreshold',

                     'windowOpenAlarmEnabled',
                     'maxMajorWindowOpenThreshold',
                     'maxCriticalWindowOpenThreshold',

                     'batteryLevelAlarmEnabled',
                     'minMajorBatteryLevelThreshold',
                     'minCriticalBatteryLevelThreshold',

                     'waterLeakAlarmEnabled',
                     'smokeAlarmEnabled',

                     'attendantPhoneNumber']
                );
            })
        );
    }

    function getCustomerId() {
        const customerAliasId = widgetContext.aliasController.getEntityAliasId('Customer');
        return widgetContext.aliasController.getAliasInfo(customerAliasId).pipe(
            widgetContext.rxjs.map((info) => {
              return {id: info.currentEntity.id, entityType: info.currentEntity.entityType};  
            })
        );
    }

}

