settings.posFunction:
var zone = data.currentZone;
var xPos = data.xPos;
var yPos = data.yPos;
var locationGroup = dsData.filter((item) => item.currentZone === zone && item.xPos === xPos && item.yPos === yPos);
if (locationGroup.length > 1) {
    const count = locationGroup.length;
    const index = locationGroup.indexOf(data);
    const radius = 0.035;
    const angle = (360 / count) * index - 45;
    const x = xPos + radius * Math.sin(angle*Math.PI/180) / aspect;
    const y = yPos + radius * Math.cos(angle*Math.PI/180);
    return {x: x, y: y};
} else {
    return {x: xPos, y: yPos};
}

settings.markerImageFunction:
var zone = data.currentZone;
var photo = data.photo;
var currentZone = '';
var zoneData = dsData.filter((data) => data.aliasName === 'Zone');
if (zoneData && zoneData.length) {
    currentZone = zoneData[0].zoneName;
}
if (photo && zone && zone === currentZone) {
    var avatarSize = 48;
    var avatarOffsetX = 0;
    var avatarOffsetY = 6;
    var svgWidth = avatarSize + 4;
    var svgHeight = avatarSize + avatarOffsetY;

    var svgStr = '<svg width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">';

    svgStr += getPhotoCircleSvg(avatarSize, photo, avatarOffsetX, avatarOffsetY);
    svgStr += getHealthStatusSvg(avatarSize, true);

    svgStr += '</svg>';

    var encodedSvg = encodeURIComponent(svgStr);

    var svgUrl = 'data:image/svg+xml,' + encodedSvg;

    return {
	    url: svgUrl,
	    size: svgHeight,
	    markerOffset: [avatarSize / 2, avatarOffsetY + avatarSize / 2],
        tooltipOffset: [0, - (avatarOffsetY + avatarSize / 2)]
	};
} else {
    return {
        url: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=',
        size: 0
    };
}

function getPhotoCircleSvg(avatarSize, photo, offsetX, offsetY) {
    var cx = avatarSize / 2;
    var cy = avatarSize / 2;
    var r = avatarSize / 2;
    return '<defs>' +
        '<clipPath id="circleView">' +
            '<circle cx="'+cx+'" cy="'+cy+'" r="'+(r-1)+'" fill="#FFFFFF" />' +
        '</clipPath>' +
    '</defs>' +
    '<g transform="translate('+offsetX+','+offsetY+')">' +
        '<circle cx="'+cx+'" cy="'+cy+'" r="'+r+'" fill="#3E3AE3" opacity="0.25"/>' +
        '<image ' +
          'width="'+avatarSize+'" ' +
          'height="'+avatarSize+'" ' +
          'href="'+photo+'" ' +
          'clip-path="url(#circleView)"' +
        '/>' +
    '</g>';
}

function getHealthStatusSvg(width, status) {
    var offsetX = width - 12;
    var offsetY = 0;
    var statusOK = '<g transform="translate('+offsetX+','+offsetY+')">' +
                     '<circle cx="8" cy="8" r="8" fill="#3AA94D" />' + 
