markerClick.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let userService = $injector.get(widgetContext.servicesMap.get('userService'));

openResidentDetailsDialog();

function openResidentDetailsDialog() {
    customDialog.customDialog(htmlTemplate, ResidentDetailsDialogController).subscribe();
}

function ResidentDetailsDialogController(instance) {

    let vm = instance;
    vm.entityName = entityName;
    vm.entityId = entityId;

    vm.widgetContext = widgetContext;
    vm.resident = { name: entityName };
    vm.wristband = null;
    vm.chartState = null;

    vm.batteryLevelClass = '';

    vm.close = function() {
        vm.dialogRef.close(null);
    };

    vm.toggleHeartRate = function() {
        if (vm.chartState !== 'heartRate') {
            vm.chartState = 'heartRate';
        } else {
            vm.chartState = null;
        }
    }

    vm.toggleTemperature = function() {
        if (vm.chartState !== 'temperature') {
            vm.chartState = 'temperature';
        } else {
            vm.chartState = null;
        }
    }

    vm.togglePanicButton = function() {
        if (vm.chartState !== 'panicButton') {
            vm.chartState = 'panicButton';
        } else {
            vm.chartState = null;
        }
    }

    vm.callResident = function() {
        if (vm.resident.phoneNumber) {
            window.open('tel:' + vm.resident.phoneNumber, '_self');
        }
    }

    vm.callEmergency = function() {
        if (vm.resident.emergencyPhoneNumber) {
            window.open('tel:' + vm.resident.emergencyPhoneNumber, '_self');
        }
    }

    let stateParams = widgetContext.stateController.getStateParams();
    stateParams.onHeartRateChartClosed = function() {
        vm.chartState = null;
    }
    stateParams.onTemperatureChartClosed = function() {
        vm.chartState = null;
    }
    stateParams.onPanicButtonChartClosed = function() {
        vm.chartState = null;
    }

    loadResident();

    function loadResident() {
        widgetContext.rxjs.forkJoin([
            userService.getUser(entityId.id),
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['photo', 'battery',
            'temperature', 'pulse', 'panicButton', 'room', 'currentRoom', 'phoneNumber', 'gender', 'birthdate',
            'emergencyFirstname', 'emergencyLastname', 'emergencyRole', 'emergencyPhoneNumber', 'chronicDiseases', 'allergy']),
            getCustomerAttributes(),
            loadResidentWristband()
        ]).subscribe(
            (data) => {
                let user = data[0];
                let attributes = data[1];
                let customerAttributes = data[2];
                vm.wristband = data[3];
                let lastUpdateTs = 0;
                for (var i = 0; i < attributes.length; i ++) {
                    lastUpdateTs = Math.max(lastUpdateTs, attributes[i].lastUpdateTs);
                }
                vm.resident.name = user.firstName + ' ' + user.lastName;
                vm.resident.photo = getAttributeValue(attributes, 'photo', '');
                vm.resident.battery = getAttributeValue(attributes, 'battery', '');
                vm.resident.temperature = getAttributeValue(attributes, 'temperature', '');
                vm.resident.pulse = getAttributeValue(attributes, 'pulse', '');
                vm.resident.room = getAttributeValue(attributes, 'room', '');
                vm.resident.currentRoom = getAttributeValue(attributes, 'currentRoom', '');
                let panicButton = getAttributeValue(attributes, 'panicButton', false);
                vm.resident.panicButton = panicButton === true ? 'On' : 'Off';
                vm.resident.gender = getAttributeValue(attributes, 'gender', '');
                vm.resident.chronicDiseases = getAttributeValue(attributes, 'chronicDiseases', '');
                vm.resident.allergy = getAttributeValue(attributes, 'allergy', '');
                let birthdate = getAttributeValue(attributes, 'birthdate', null);
                if (birthdate !== null) {
                    let birthday = new Date(birthdate);
                    var ageDifMs = Date.now() - birthday.getTime();
                    var ageDate = new Date(ageDifMs);
                    vm.resident.age = Math.abs(ageDate.getUTCFullYear() - 1970);
                } else {
                    vm.resident.age = '';
                }
                vm.resident.phoneNumber = getAttributeValue(attributes, 'phoneNumber', null);
                vm.resident.emergencyPhoneNumber = getAttributeValue(attributes, 'emergencyPhoneNumber', null);
                let emergencyFirstname = getAttributeValue(attributes, 'emergencyFirstname', '');
                let emergencyLastname = getAttributeValue(attributes, 'emergencyLastname', '');
                let emergencyRole = getAttributeValue(attributes, 'emergencyRole', '');
                vm.resident.emergencyContactText = emergencyFirstname + ' ' + emergencyLastname;
                if (emergencyRole) {
                    vm.resident.emergencyContactText += ', ' + emergencyRole;
                }

                let measureUpdateStr = widgetContext.milliSecondsToTimeString.transform(Date.now() - lastUpdateTs, true);
                let measureUpdateParts = measureUpdateStr.split(' ');
                if (measureUpdateParts.length > 4) {
                    measureUpdateParts.splice(4 - measureUpdateParts.length);
                    measureUpdateStr = measureUpdateParts.join(' ');
                }

                vm.resident.measureUpdate = measureUpdateStr + ' ago';
                if (vm.resident.battery !== '') {
                    let batteryLevelAlarmEnabled = getAttributeValue(customerAttributes, 'batteryLevelAlarmEnabled', false);
                    if (batteryLevelAlarmEnabled) {
                        let minCriticalBatteryLevelThreshold = getAttributeValue(customerAttributes, 'minCriticalBatteryLevelThreshold', 0);
                        if (vm.resident.battery <= minCriticalBatteryLevelThreshold) {
                            vm.batteryLevelClass = 'al-critical';
                        } else {
                            let minMajorBatteryLevelThreshold = getAttributeValue(customerAttributes, 'minMajorBatteryLevelThreshold', 0);
                            if (vm.resident.battery <= minMajorBatteryLevelThreshold) {
                                vm.batteryLevelClass = 'al-major';
                            }
                        }
                    }
                }
            }
        );
    }

    function getAttributeValue(attributes, key, defaultValue) {
        let value = null;
        let found = attributes.filter((attribute) => attribute.key === key);
        if (found.length) {
            value = found[0].value;
        }
        if (value === null || value === '') {
            value = defaultValue;
        }
        return value;
    }

    function loadResidentWristband() {
        var wristbandQuery = {
            parameters: {
                rootId: entityId.id,
                rootType: entityId.entityType,
                direction: 'FROM',
                maxLevel: 1
            },
            relationType: 'Use',
            deviceTypes: ['AL_Wristband']
        };
        return deviceService.findByQuery(wristbandQuery).pipe(
            widgetContext.rxjs.map((wristbands) => {
                if (wristbands.length) {
                    return wristbands[0];
                } else {
                    return null;
                }
            })
        );
    }

    function getCustomerAttributes() {
        return getCustomerId().pipe(
            widgetContext.rxjs.switchMap((customerId) => {
                return attributeService.getEntityAttributes(customerId, 'SERVER_SCOPE', ['batteryLevelAlarmEnabled', 'minMajorBatteryLevelThreshold', 'minCriticalBatteryLevelThreshold']);
            })
        );
    }

    function getCustomerId() {
        const customerAliasId = widgetContext.aliasController.getEntityAliasId('Customer');
        return widgetContext.aliasController.getAliasInfo(customerAliasId).pipe(
            widgetContext.rxjs.map((info) => {
              return {id: info.currentEntity.id, entityType: info.currentEntity.entityType};  
            })
        );
    }

}

