settings.markerImageFunction:
var type = data['type'];

if (type) {
    switch(type) {
        case 'AL_Room':
            return getRoomMarker();
        case 'AL_Gateway':
            return {
                url: images[0],
                size: 40,
                markerOffset: [20, 20],
                tooltipOffset: [0, -20]
            };
        case 'AL_RoomSensor':
            return {
                url: images[1],
