            };
        case 'AL_DoorSensor':
            return {
                url: images[2],
                size: 40,
                markerOffset: [20, 20],
                tooltipOffset: [0, -20]
            };
        case 'AL_WindowSensor':
            return {
                url: images[3],
                size: 40,
                markerOffset: [20, 20],
                tooltipOffset: [0, -20]
            };
        case 'AL_LeakSensor':
            return {
                url: images[4],
                size: 40,
                markerOffset: [20, 20],
                tooltipOffset: [0, -20]
            };
        case 'AL_SmokeSensor':
            return {
                url: images[5],
                size: 40,
                markerOffset: [20, 20],
                tooltipOffset: [0, -20]
            };
        }
}

return {
    url: 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=',
    size: 0
};

function getRoomMarker() {
    var text = getRoomText();
    var defaultMarkerWidth = 24;
    var textWidth = getTextWidth(text);
    var width = defaultMarkerWidth + textWidth;
    var svgStr = getRoomSvg(width, text);
    var encodedSvg = encodeURIComponent(svgStr);
    var svgUrl = 'data:image/svg+xml,' + encodedSvg;
    return {
        url: svgUrl,
        size: width,
        markerOffset: [width / 2, defaultMarkerWidth / 2],
        tooltipOffset: [0, - (defaultMarkerWidth / 2)]
    };
}

function getRoomSvg(width, text) {
    var color = '#344082';
    var backgroundOpacity = 0.1;
    var background = getRoomBackground(color, backgroundOpacity, width);
    var svgWidth = width;
    var svgHeight = 24;
    var opacity = 1;
    var roomSvg = '<svg opacity="' + opacity + '" width="' + svgWidth + '" height="' + svgHeight + '" viewBox="0 0 ' + svgWidth + ' ' + svgHeight + '" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                    background;
    if (text !== '') {
        roomSvg += '<text x="50%" y="55%" dominant-baseline="middle" text-anchor="middle" fill="'+color+'" xml:space="preserve" style="white-space: pre" font-family="Roboto" font-size="14" font-weight="500" letter-spacing="0em">' + text + '</text>';
    }
    roomSvg += '</svg>';
    return roomSvg;    
}

function getRoomBackground(color, opacity, width) {
     if (width === 24) {
         return '<circle cx="12" cy="12" r="12" opacity="'+opacity+'" fill="'+ color +'"/>';
     } else {
         return '<g opacity="'+opacity+'"><circle cx="12" cy="12" r="12" fill="'+ color +'"/>' + 
                '<circle cx="' + (width - 12) + '" cy="12" r="12" fill="'+ color +'"/>' +
                '<rect x="12" y="0" width="'   + (width - 24) + '" height="24" rx="1" fill="'+ color +'"/></g>';
     }
}

function getRoomText() {
    if (typeof data['name'] !== 'undefined') {
        return data['name'] + '';
    } else {
        return '';
    }
}

function getTextWidth(text) {
    var width = 0;
    for (var i = 0; i < text.length; i++) {
        var charWidth;
        var character = text.charAt(i);
        if (character === ' ') {
            charWidth = 8;
        } else if (character === '-') {
            charWidth = 7;
        } else if (character === '') {
            charWidth = 5;
        } else if (!isNaN(character * 1)){
            charWidth = 8;
        } else {
            // Upper case
            if (character === character.toUpperCase()) {
                charWidth = 9;
            // Lower case    
            } else {
                charWidth = 7;
            }
        }
        width += charWidth;
    }
    return width;
}

