let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditDeviceDialog();

function openEditDeviceDialog() {
    customDialog.customDialog(htmlTemplate, EditDeviceDialogController).subscribe();
}

function EditDeviceDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entityId = entityId;

    vm.widgetContext = widgetContext;
    vm.device = null;

    vm.location = null;
    vm.hasLocation = false;
    vm.locationChanged = false;
    vm.locationSubscription = null;


    vm.editDeviceFormGroup = vm.fb.group({
      name: [''],
      label: [null],
    });

    vm.editDeviceFormGroup.disable();

    vm.cancel = function() {
        cancelLocationSubscription();
        restoreLocation().subscribe(
            function () {
                vm.dialogRef.close(null);
            }
        );
    };

    vm.save = function() {
        cancelLocationSubscription();
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
    };

    loadDevice();

    function createLocationSubscription() {
        var subscriptionInfo = {
            type: 'entity',
            entityType: vm.device.id.entityType,
            entityId: vm.device.id.id,
            attributes: [{ name: 'xPos' }, {name: 'yPos'}]
        };
        var options = {
            callbacks: {
                onDataUpdated: function(subscription) {
                    checkLocation(subscription.data);
                }
            }
        };
        return widgetContext.subscriptionApi.createSubscriptionFromInfo(
                            'latest', [ subscriptionInfo ], options, false, true).pipe(
            widgetContext.rxjs.tap(function(locationSubscription) {
                vm.locationSubscription = locationSubscription;
            })
        );

    }

    function checkLocation(data) {
        var hasLocation = false;
        var xPos, yPos;
        if (data.length === 2) {
            for (var i = 0; i < data.length; i ++) {
                var datasourceData = data[i];
                if (datasourceData.dataKey.name === 'xPos') {
                    xPos = datasourceData.data[0][1];
                } else if (datasourceData.dataKey.name === 'yPos') {
                    yPos = datasourceData.data[0][1];
                }
            }
            if (isNumeric(xPos) && isNumeric(yPos)) {
                hasLocation = true;
            }
        }
        var locationChanged = false;
        if (hasLocation && !vm.location || !hasLocation && vm.location || 
            vm.location[0] !== xPos || vm.location[1] !== yPos) {
            locationChanged = true;
        }
        vm.hasLocation = hasLocation;
        vm.locationChanged = locationChanged;
    }

    function isNumeric(val) {
        if (typeof val === "number") {
            return true;
        } else if (typeof val === "string") {
            return !isNaN(val) && 
                !isNaN(parseFloat(val));
        } else {
            return false;
        }
    }

    function cancelLocationSubscription() {
        if (vm.locationSubscription) {
            widgetContext.subscriptionApi.removeSubscription(vm.locationSubscription.id);
            vm.locationSubscription = null;
        }
    }

    function restoreLocation() {
        if (vm.locationChanged) {
            var xPos = null;
            var yPos = null;
            if (vm.location && vm.location.length === 2) {
                xPos = vm.location[0];
                yPos = vm.location[1];
            }
            return attributeService.saveEntityAttributes(vm.device.id, "SERVER_SCOPE",
                [
                    { key: 'xPos', value: xPos },
                    { key: 'yPos', value: yPos }
                ]
            );
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function loadDevice() {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['xPos', 'yPos']),
            deviceService.getDevice(entityId.id)
        ]).subscribe(
            function (data) {
                var attributes = data[0];
                for (var i = 0; i < attributes.length; i ++) {
                    if (attributes[i].key === 'xPos' || attributes[i].key === 'yPos') {
                        if (!vm.location) {
                            vm.location = [];
                        }
                        vm.location[attributes[i].key === 'xPos' ? 0 : 1] = attributes[i].value;
                    }
                }
                vm.device = data[1];
                vm.editDeviceFormGroup.get('name').patchValue(vm.device.name, {emitEvent: false});
                vm.editDeviceFormGroup.get('label').patchValue(vm.device.label, {emitEvent: false});
                createLocationSubscription();
            }
        );
    }

}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

openUnassignDeviceDialog();

function openUnassignDeviceDialog() {
    let title = 'Un-assign device \'' +
                 entityName + '\'';
    let content = 'Are you sure you want to un-assign device \'' + entityName + '\'?';
    dialogs.confirm(title, content, 'Cancel', 'Un-assign').subscribe(
        function(result) {
            if (result) {
                unassignDevice();
            }
        }
    );
    $('tb-confirm-dialog').addClass('al-delete-dialog');
}

function unassignDevice() {
    unassignDeviceObservable().subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function unassignDeviceObservable() {
    return deviceService.getDevice(entityId.id).pipe(
        widgetContext.rxjs.switchMap((device) => {
            return getAssignedUnassignedDeviceGroupIds().pipe(
                widgetContext.rxjs.switchMap((groupIds) => {
                    device.label = '';
                    let deleteAttributesArray = [
                        {key: 'xPos'},
                        {key: 'yPos'},
                        {key: 'roomXPos'},
                        {key: 'roomYPos'},
                        {key: 'room'},
                        {key: 'zone'}
                    ];
                    return widgetContext.rxjs.forkJoin([
                        deviceService.saveDevice(device),
                        attributeService.deleteEntityAttributes(device.id, 'SERVER_SCOPE', deleteAttributesArray),
                        deleteRoomRelation(),
                        entityGroupService.removeEntityFromEntityGroup(groupIds.assignedGroupId, device.id.id),
                        entityGroupService.addEntityToEntityGroup(groupIds.unassignedGroupId, device.id.id)
                    ]);
                })
            );
        })
    );
}

function deleteRoomRelation() {
    var roomQuery = {
        parameters: {
            rootId: entityId.id,
            rootType: entityId.entityType,
            direction: 'TO',
            maxLevel: 1
        },
        relationType: 'Contains',
        assetTypes: ['AL_Room']
    };
    return assetService.findByQuery(roomQuery).pipe(
        widgetContext.rxjs.switchMap((rooms) => {
            if (rooms.length) {
                return entityRelationService.deleteRelation(rooms[0].id, 'Contains', entityId);
            } else {
                return widgetContext.rxjs.of(null);
            }
        })
    );
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while device un-assign. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

function getAssignedUnassignedDeviceGroupIds() {
    return getCustomerId().pipe(
        widgetContext.rxjs.switchMap((ownerId) => {
            return widgetContext.rxjs.forkJoin([
                getOrCreateEntityGroup(ownerId, 'DEVICE', 'Unassigned Devices'),
                getOrCreateEntityGroup(ownerId, 'DEVICE', 'Assigned Devices')
            ]).pipe(
                widgetContext.rxjs.map((groups) => {
                    return { unassignedGroupId: groups[0].id.id, assignedGroupId: groups[1].id.id };
                })
            );
        })
    );
}

function getOrCreateEntityGroup(ownerId, type, name) {
      return getEntityGroupByName(ownerId, name, type).pipe(
          widgetContext.rxjs.switchMap((group) => {
              if (group) {
                  return widgetContext.rxjs.of(group);
              } else {
                  var newGroup = {
                    type: type,
                    name: name,
                    ownerId: ownerId
                  };
                  return entityGroupService.saveEntityGroup(newGroup);
              }
          })
      );
}

function getEntityGroupByName(ownerId, groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByOwnerIdAndPageLink(ownerId.entityType, ownerId.id, groupType, entityGroupsPageLink, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
}


function getCustomerId() {
    const customerAliasId = widgetContext.aliasController.getEntityAliasId('Customer');
    return widgetContext.aliasController.getAliasInfo(customerAliasId).pipe(
        widgetContext.rxjs.map((info) => {
          return {id: info.currentEntity.id, entityType: info.currentEntity.entityType};  
        })
    );
}

