if (data[0] && data[1]) {
    let roomName = data[0]['entityName'];
    let zoneName = data[1]['entityName'];
    let html = '<section style="width: 100%; height: 100%;" fxLayout="column">' +
              '<section class="location-title" fxLayout="row" fxLayoutAlign="start center">' +
                roomName + '. ' + zoneName +
              '</section>' +
              '<tb-dashboard-state fxFlex [ctx]="ctx" [entityId]="{ id: \'' + data[0].entityId + '\', entityType: \'' + data[0].entityType + '\'  }" stateId="room_location"></tb-dashboard-state>';
    let stateParams = ctx.dashboard.stateController.getStateParams();
    if (stateParams.locationLastUpdateTs) {
        let elapsed = Date.now() - stateParams.locationLastUpdateTs;
        let measureUpdateStr = ctx.milliSecondsToTimeString.transform(elapsed, true) + ' ago';
        html += '<section class="location-footer" fxLayout="row" fxLayoutAlign="start center">' +
                    '<span class="measure-update-label">Measure update</span><span class="measure-update-value">' + measureUpdateStr + '</span>' +
                '</section>';
    }
    html += '</section>';
    return html;
} else {
    return '';
}

