if (data[0] && data[0].firstName) {
    let heartRate = 'N/A';
    let bodyTemperature = 'N/A';
    let panicButton = 'N/A';
    let batteryLevel = 'N/A';
    let measuresUpdate = 'N/A';
    let lastUpdate = 0;
    if (typeof data[0].pulse !== 'undefined') {
        heartRate = data[0].pulse + ' bpm';
        lastUpdate = data[0]['pulse|ts'];
    }
    if (typeof data[0].temperature !== 'undefined') {
        bodyTemperature = data[0].temperature + ' C';
        if (data[0]['temperature|ts'] > lastUpdate) {
            lastUpdate = data[0]['temperature|ts'];
        }
    }
    if (typeof data[0].panicButton !== 'undefined') {
        panicButton = data[0].panicButton === 'true' ? 'On' : 'Off';
        if (data[0]['panicButton|ts'] > lastUpdate) {
            lastUpdate = data[0]['panicButton|ts'];
        }
    }
    if (typeof data[0].battery !== 'undefined') {
        batteryLevel = data[0].battery + '%';
        if (data[0]['battery|ts'] > lastUpdate) {
            lastUpdate = data[0]['battery|ts'];
        }
    }
    if (lastUpdate > 0) {
        let measureLastDuration = Date.now() - lastUpdate;
        measuresUpdate = ctx.milliSecondsToTimeString.transform(measureLastDuration, true) + ' ago';
    }
    return '<section style="width: 100%; height: 100%;" fxLayout="column">' +
                '<section class="resident-title">' + data[0].firstName + ' ' + data[0].lastName + '</section>' +
                '<section fxFlex fxLayout="row" fxLayoutGap="16px">' +
