                        '<section>Measures update</section>' +
                    '</section>' +
                    '<section class="resident-values" fxFlex fxLayout="column" fxLayoutAlign="space-around start">' +
                        '<section>' + heartRate + '</section>' +
                        '<section>' + bodyTemperature + '</section>' +
                        '<section>' + panicButton + '</section>' +
                        '<section>' + batteryLevel + '</section>' +
                        '<section>' + measuresUpdate + '</section>' +
                    '</section>' +
                '</section>' +
                '<section fxLayout="row" fxLayoutAlign="center" style="padding-top: 8px;">' +
                    '<button class="resident-details" id="resident-details" mat-button><span>Details</span><mat-icon>chevron_right</mat-icon></button>' +
                '</section>' +
           '</section>';
} else {
