actionCellButton.customFunction:
const POSTAL_CODE_PATTERNS = {
  'United States': '(\\d{5}([\\-]\\d{4})?)',
  'Australia': '[0-9]{4}',
  'Austria': '[0-9]{4}',
  'Belgium': '[0-9]{4}',
  'Brazil': '[0-9]{5}[\\-]?[0-9]{3}',
  'Canada': '^(?!.*[DFIOQU])[A-VXY][0-9][A-Z][ -]?[0-9][A-Z][0-9]$',
  'Denmark': '[0-9]{3,4}',
  'Faroe Islands': '[0-9]{3,4}',
  'Netherlands': '[1-9][0-9]{3}\\s?[a-zA-Z]{2}',
  'Germany': '[0-9]{5}',
  'Hungary': '[0-9]{4}',
  'Italy': '[0-9]{5}',
  'Japan': '\\d{3}-\\d{4}',
  'Luxembourg': '(L\\s*(-|—|–))\\s*?[\\d]{4}',
  'Poland': '[0-9]{2}\\-[0-9]{3}',
  'Spain': '((0[1-9]|5[0-2])|[1-4][0-9])[0-9]{3}',
  'Sweden': '\\d{3}\\s?\\d{2}',
  'United Kingdom': '[A-Za-z]{1,2}[0-9Rr][0-9A-Za-z]? [0-9][ABD-HJLNP-UW-Zabd-hjlnp-uw-z]{2}'
};

let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let customerService = $injector.get(widgetContext.servicesMap.get('customerService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditCompanyDialog();

function openEditCompanyDialog() {
  customDialog.customDialog(htmlTemplate, EditCompanyDialogController).subscribe();
}

function EditCompanyDialogController(instance) {
  let vm = instance;

  vm.customer = {};

  vm.editCompanyFormGroup = vm.fb.group({
    title: ['', [vm.validators.required, vm.validators.maxLength(255)]]
  });

  vm.editCompanyFormGroup.addControl('country', vm.fb.control('', []));
  vm.editCompanyFormGroup.addControl('city', vm.fb.control('', []));
  vm.editCompanyFormGroup.addControl('state', vm.fb.control('', []));
  vm.editCompanyFormGroup.addControl('zip', vm.fb.control('',
      zipValidators('')
  ));
  vm.editCompanyFormGroup.addControl('address', vm.fb.control('', []));
  vm.editCompanyFormGroup.addControl('address2', vm.fb.control('', []));
  vm.editCompanyFormGroup.addControl('phone', vm.fb.control('', []));
  vm.editCompanyFormGroup.addControl('email', vm.fb.control('', [vm.validators.email]));


  vm.editCompanyFormGroup.get('country').valueChanges.subscribe(
      (country) => {
        vm.editCompanyFormGroup.get('zip').setValidators(zipValidators(country));
        vm.editCompanyFormGroup.get('zip').updateValueAndValidity({onlySelf: true});
        vm.editCompanyFormGroup.get('zip').markAsTouched({onlySelf: true});
      }
  );

  getCompany();


  vm.cancel = function () {
    vm.dialogRef.close(null);
  };

  vm.save = function () {
    vm.editCompanyFormGroup.markAsPristine();
    saveCompanyObservable().subscribe(
      function (customer) {
          widgetContext.rxjs.forkJoin([
              saveAttributes(customer)
          ]).subscribe(
              function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
              }
        );
      }
    );
  };

  function getCompany() {
      customerService.getCustomer(entityId.id).subscribe(
          (customer) => {
                vm.customer = customer;          
                vm.editCompanyFormGroup.patchValue({
                  title: vm.customer.title,
                  country: vm.customer.country,
                  city: vm.customer.city,
                  state: vm.customer.state,
                  zip: vm.customer.zip,
                  address: vm.customer.address,
                  address2: vm.customer.address2,
                  phone: vm.customer.phone,
                  email: vm.customer.email
                }, {emitEvent: false});
                vm.editCompanyFormGroup.get('zip').setValidators(zipValidators(vm.customer.country));
                vm.editCompanyFormGroup.get('zip').updateValueAndValidity({onlySelf: true});
          }
    );
  }

  function zipValidators(country) {
    const zipValidators = [];
    if (country && POSTAL_CODE_PATTERNS[country]) {
      const postalCodePattern = POSTAL_CODE_PATTERNS[country];
      zipValidators.push(vm.validators.pattern(postalCodePattern));
    }
    return zipValidators;
  }

  function saveCompanyObservable() {
      const formValues = vm.editCompanyFormGroup.value;
      vm.customer.title = formValues.title;
      vm.customer.country = formValues.country;
      vm.customer.city = formValues.city;
      vm.customer.state = formValues.state;
      vm.customer.zip = formValues.zip;
      vm.customer.address = formValues.address;
      vm.customer.address2 = formValues.address2;
      vm.customer.phone = formValues.phone;
      vm.customer.email = formValues.email;
      return customerService.saveCustomer(vm.customer);
  }

  function saveAttributes(customer) {
    let attributesArray = [
        {
            key: 'type',
            value: 'retail'
        },
        {
            key: 'address',
            value: getAddressValue(customer)
        }
    ];
    return attributeService.saveEntityAttributes(customer.id, "SERVER_SCOPE", attributesArray);
  }

  function getAddressValue(customer) {
      var address = '';
      if (customer.address) {
          address += customer.address;
      }
      if (customer.address2) {
          address += (address ? ', ' : '') + customer.address2;
      }
      if (customer.city) {
          address += (address ? ', ' : '') + customer.city;
      }
      if (customer.state) {
          address += (address ? ', ' : '') + customer.state;
      }
      if (customer.zip) {
          address += (customer.state ? ' ' : (address ? ', ' : '')) + customer.zip;
      }
      if (customer.country) {
          address += (address ? ', ' : '') + customer.country;
      }
      return address;
  }
}

var $injector = widgetContext.$scope.$injector;
var dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
var customerService = $injector.get(widgetContext.servicesMap.get('customerService'));

openDeleteCustomerDialog();

function openDeleteCustomerDialog() {
  var title = 'Are you sure you want to delete the company ' + entityName + '?';
  var content = 'Be careful, after the confirmation, the company and all related data will become unrecoverable!';
  dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
    function(result) {
      if (result) {
        deleteCustomer();
      }
    }
  );
}

function deleteCustomer() {
  customerService.deleteCustomer(entityId.id).subscribe(
    function() {
      widgetContext.updateAliases();
    }
  );
}

