let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

openAddSupermarketDialog();

function openAddSupermarketDialog() {
  customDialog.customDialog(htmlTemplate, AddSupermarketDialogController).subscribe();
}

function AddSupermarketDialogController(instance) {
  let vm = instance;

  vm.addSupermarketFormGroup = vm.fb.group({
    name: ['', [vm.validators.required]]
  });

  vm.cancel = function () {
    vm.dialogRef.close(null);
  };

  vm.save = function () {
    var customerId;
    if (widgetContext.currentUser.authority === 'TENANT_ADMIN') {
        customerId = widgetContext.stateController.getStateParams().entityId;
    } else {
        customerId = { id: widgetContext.currentUser.customerId, entityType: 'CUSTOMER'};
    }
    vm.addSupermarketFormGroup.markAsPristine();
    saveSupermarketObservable(customerId).subscribe(
      function (supermarket) {
          widgetContext.rxjs.forkJoin([
              saveCustomerToSupermarketRelation(customerId, supermarket.id),
              saveAttributes(supermarket.id)
          ]).subscribe(
              function () {
                var params = widgetContext.stateController.getStateParams();
                var selectedSupermarket = params['selectedSupermarket'];
                params['selectedSupermarket'] = { entityId: supermarket.id, entityName: supermarket.name, entityLabel: '' };
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
              }
        );
      }
    );
  };

  function saveSupermarketObservable(customerId) {
    return getSupermarketsGroup(customerId).pipe(
        widgetContext.rxjs.switchMap((supermarketsGroup) => {
            const formValues = vm.addSupermarketFormGroup.value;
            let supermarket = {
              name: formValues.name,
              type: 'supermarket',
              customerId: customerId
            };
            return assetService.saveAsset(supermarket, supermarketsGroup.id.id)
        })
    );
  }

  function getSupermarketsGroup(customerId) {
      return entityGroupService.getEntityGroupsByOwnerId(customerId.entityType, customerId.id, 'ASSET').pipe(
          widgetContext.rxjs.switchMap((entityGroups) => {
              var supermarketsGroup = entityGroups.find(group => group.name === 'Supermarkets');
              if (supermarketsGroup) {
                  return widgetContext.rxjs.of(supermarketsGroup);
              } else {
                  supermarketsGroup = {
                      type: 'ASSET',
                      name: 'Supermarkets',
                      ownerId: customerId
                  };
                  return entityGroupService.saveEntityGroup(supermarketsGroup);
              }
          })
      );
  }

  function saveCustomerToSupermarketRelation(customerId, supermarketId) {
      var relation = {
          from: customerId,
          to: supermarketId,
          typeGroup: 'COMMON',
          type: 'Owns'
      };
      return entityRelationService.saveRelation(relation);
  }

  function saveAttributes(entityId) {
    let attributesArray = [
        {
            key: 'latitude',
            value: 37.780108
        },
        {
            key: 'longitude',
            value: -122.447053
        },
        {
            key: 'address',
            value: 'N/A'
        },
        {
            key: 'state',
            value: 'normal'
        },
        {
            key: 'floorplan',
            value: 'data:image/svg+xml;base64,'
        }
    ];
    return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
  }
}

settings.cellContentFunction:
var state = value;
var active = entity.active;
if (active === 'false') {
    state = 'disconnected';
}

var stateSvg = getStateSvg(state);
var encodedStateSvg = encodeURIComponent(stateSvg);
var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
var stateLabel = getStateLabel(state);

return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
       '</span>';

function getStateSvg(state) {
    switch (state) {
        case 'critical':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
                   '</svg>';
        case 'major':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#D5C21B"/>' +
                   '</svg>';
        case 'normal':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#1F8B4D"/>' +
                   '</svg>';
        case 'disconnected':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<g clip-path="url(#clip0_2839_52564)">' +
                   '<circle cx="10" cy="10" r="6" fill="#CAC1D2"/>' +
                   '<path d="M1.92896 16.728L16.0711 2.58589L17.4853 4.00011L3.34317 18.1422L1.92896 16.728Z" fill="#CAC1D2"/>' +
                   '<path d="M2.10692 16.3171C1.81393 16.0241 1.81393 15.5491 2.10692 15.2561L15.1852 2.17788C15.4781 1.88489 15.9532 1.88489 16.2462 2.17788C16.5392 2.47088 16.5392 2.94591 16.2462 3.2389L3.16794 16.3171C2.87495 16.6101 2.39991 16.6101 2.10692 16.3171Z" fill="white"/>' +
                   '</g>' +
                   '<defs>' +
                   '<clipPath id="clip0_2839_52564">' +
                   '<rect width="20" height="20" fill="white"/>' +
                   '</clipPath>' +
                   '</defs>' +
                   '</svg>';
        default:
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
                   '</svg>';
    }
}

function getStateLabel(state) {
    switch(state) {
        case 'critical':
            return 'Critical';
        case 'major':
            return 'Major';
        case 'normal':
            return 'Normal';
        case 'disconnected':
            return 'Disconnected';
        default:
            return state;
    }
}

