actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openEditDeviceDialog();

function openEditDeviceDialog() {
  customDialog.customDialog(htmlTemplate, EditDeviceDialogController).subscribe();
}

function EditDeviceDialogController(instance) {
  let vm = instance;

  vm.deviceTypes = [
      'Chiller',
      'Door Sensor',
      'Freezer',
      'Liquid Level Sensor',
      'Motion Sensor',
      'Occupancy Sensor',
      'Smart Bin',
      'Smart Shelf',
      'Smoke Sensor'
  ];

  vm.device = {};

  vm.editDeviceFormGroup = vm.fb.group({
    name: ['', [vm.validators.required]],
    type: ['', [vm.validators.required]],
    label: ['', [vm.validators.required]]
  });

  getDevice();

  vm.cancel = function() {
    vm.dialogRef.close(null);
  };

  vm.save = function() {
    vm.editDeviceFormGroup.markAsPristine();
    saveDevice().subscribe(
      function () {
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
      }
    );
  };

  function getDevice() {
      deviceService.getDevice(entityId.id).subscribe(
          function (device) {
                vm.device = device;          
                vm.editDeviceFormGroup.patchValue({
                  name: vm.device.name,
                  type: vm.device.type,
                  label: vm.device.label
                }, {emitEvent: false});
              }
    );
  }

  function saveDevice() {
    const formValues = vm.editDeviceFormGroup.value;
    vm.device.name = formValues.name;
    if (vm.device.type !== formValues.type) {
        vm.device.type = formValues.type;
        vm.device.deviceProfileId = null;
    }
    vm.device.label = formValues.label;
    return deviceService.saveDevice(vm.device);
  }

}

var $injector = widgetContext.$scope.$injector;
var dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
var deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openDeleteDeviceDialog();

function openDeleteDeviceDialog() {
  var title = 'Are you sure you want to delete the device ' + entityName + '?';
  var content = 'Be careful, after the confirmation, the device and all related data will become unrecoverable!';
  dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
    function(result) {
      if (result) {
        deleteDevice();
      }
    }
  );
}

function deleteDevice() {
  deviceService.deleteDevice(entityId.id).subscribe(
    function() {
      widgetContext.updateAliases();
    }
  );
}

settings.colorFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'colorpin') {
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120 * 100;
	    return tinycolor.mix('blue', 'red', amount = percent).toHexString();
	}
	return 'blue';
}

settings.labelFunction:
var deviceType = data['Type'];
var coordinates = data['coordinates'];
var status = 'normal';
if (deviceType === 'Smart Shelf') {
    if (coordinates && coordinates.length === 4) {
        var color = getColor(status);
        var label = 'Vegetables';
        return '<span style="margin-left: 100px; margin-top: -50px; color: '+ color +';">' + label + '</span>';
    }
}
return '';

function getColor(status) {
    switch(status) {
        case 'major':
            return '#D5C21B';
        case 'critical':   
            return '#E64A19';
        case 'off':
        case 'disconnected':
            return '#CAC1D2';
        case 'normal':
        default:    
            return '#817889';
    }
}

