  });

  getUser();

  vm.cancel = function () {
    vm.dialogRef.close(null);
  };

  vm.save = function () {
    vm.editUserFormGroup.markAsPristine();
    saveUserObservable().subscribe(
      function () {
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
      }
    );
