settings.markdownCss:
.cards-container {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

.card {
    display: flex;
    flex-direction: row;
}

.divider {
    position: absolute;
    top: 0;
    width: 3px;
    height: 100%;
    border-radius: 2px;
    border: 1px solid rgba(31, 70, 144, 1);
    background-color: rgba(31, 70, 144, 1);
}
.divider-blue .divider {
    border: 1px solid #002878;
    background-color: #002878;
}

.divider-blue .card-value {
    color: #002878;
}

.divider-gray .divider {
    border: 1px solid #999999;
    background-color: #999999;
}

.divider-gray .card-value {
    color: #999999;
}

.card-content {
    padding-left: 10px;
}
.card-label {
    color: rgba(0, 0, 0, 0.38);
    text-align: justify;
    font-family: Roboto;
    font-size: 12px;
    font-style: normal;
    font-weight: 400;
    line-height: 19px;
    letter-spacing: 0.25px;
    margin-right: 4px;
}
@media screen and (max-width: 1020px) {
    .card-label {
        display: none;
    }
}
.card-value {
    text-align: justify;
    font-family: Roboto;
    font-size: 14px;
    font-style: normal;
    font-weight: 400;
    line-height: 20px;
    letter-spacing: 0.2px;
}

