actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog')),
    attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditDeviceDialog();

function openEditDeviceDialog() {
    customDialog.customDialog(htmlTemplate, EditDeviceDialogController).subscribe();
}

function EditDeviceDialogController(instance) {
    let vm = instance;

    vm.attributes = {};
    let attributesKeys = ['dailyConsumptionThreshold', 'weeklyConsumptionThreshold',
        'lowBattThreshold', 'lowTempThreshold', 'inactivityTimeout'];

    vm.editDeviceFormGroup = vm.fb.group({
        dailyConsumptionThreshold: [null],
        weeklyConsumptionThreshold: [null],
        lowBattThreshold: [null],
        lowTempThreshold: [null],
        inactivityTimeout: [null, [vm.validators.pattern(/^-?[0-9]+$/)]]
    });

    getEntityInfo(attributesKeys);

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = () => {
        vm.loading = true;
        vm.editDeviceFormGroup.markAsPristine();
        saveAttributes(entityId).subscribe(
            () => {
                vm.loading = false;
                vm.dialogRef.close(null);
            },
            () => {
                vm.loading = false;
            }
        );
    };

    function getEntityInfo(attributesKeys) {
        vm.loading = true;
        attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', attributesKeys).subscribe(
            (data) => {
                if (data.length) {
                    getEntityAttributes(data);
                    vm.editDeviceFormGroup.patchValue(vm.attributes, {emitEvent: false});
                }
                vm.loading = false;
            }
        );
    }

    function getEntityAttributes(attributes) {
        for (let i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function saveAttributes(entityId) {
        let attributes = vm.editDeviceFormGroup.value;
        let attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== vm.attributes[key]) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }
}

var $injector = widgetContext.$scope.$injector;
var dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
var deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openDeleteDeviceDialog();

function openDeleteDeviceDialog() {
  var title = 'Are you sure you want to delete the device ' + entityName + '?';
  var content = 'Be careful, after the confirmation, the device and all related data will become unrecoverable!';
  dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
    function(result) {
      if (result) {
        deleteDevice();
      }
    }
  );
}

function deleteDevice() {
  deviceService.deleteDevice(entityId.id).subscribe(
    function() {
      widgetContext.updateAliases();
    }
  );
}

