let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog')),
    deviceService = $injector.get(widgetContext.servicesMap.get('deviceService')),
    attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditDeviceDialog();

function openEditDeviceDialog() {
    customDialog.customDialog(htmlTemplate, EditDeviceDialogController).subscribe();
}

function EditDeviceDialogController(instance) {
    let vm = instance;

    vm.device = {};
    vm.attributes = {};
    let attributesKeys = ['installationDate'];

    vm.editDeviceFormGroup = vm.fb.group({
        deviceName: [entityName, [vm.validators.required]],
        deviceLabel: [entityLabel],
        attributes: vm.fb.group({
            installationDate: [null]
        })
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = () => {
        vm.loading = true;
        vm.editDeviceFormGroup.markAsPristine();
        widgetContext.rxjs.forkJoin([saveDevice(), saveAttributes(entityId)]).subscribe(
            () => {
                vm.loading = false;
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            },
            () => {
                vm.loading = false;
            }
        );
    };

    function getEntityInfo() {
        vm.loading = true;
        widgetContext.rxjs.forkJoin([
            deviceService.getDevice(entityId.id),
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', attributesKeys)]).subscribe(
            (data) => {
                vm.device = data[0];
                if (data[1].length) {
                    getEntityAttributes(data[1]);
                    vm.editDeviceFormGroup.patchValue(
                        {attributes: vm.attributes},
                        {emitEvent: false}
                        );
                }
                vm.loading = false;
            }
        );
    }

    function saveDevice() {
        const formValues = vm.editDeviceFormGroup.value;
        if (vm.device.name !== formValues.deviceName || (vm.device.label !== formValues.deviceLabel)) {
            vm.device.name = formValues.deviceName;
            vm.device.label = formValues.deviceLabel;
            return deviceService.saveDevice(vm.device);
        }
        return widgetContext.rxjs.of([]);
    }

    function getEntityAttributes(attributes) {
        for (let i = 0; i < attributes.length; i++) {
            if (attributes[i].key === 'installationDate') {
                vm.attributes[attributes[i].key] = moment(attributes[i].value).toDate();
            } else {
                vm.attributes[attributes[i].key] = attributes[i].value;
            }
        }
    }

    function saveAttributes(entityId) {
        let attributes = vm.editDeviceFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== vm.attributes[key]) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }
}

