    vm.customer.address = formValues.address;

    vm.user = {};
    vm.user.email = formValues.email;
    vm.user.firstName = formValues.firstName;
    vm.user.lastName = formValues.lastName;

    vm.attributes = {};
    vm.attributes.firstName = formValues.firstName;
    vm.attributes.lastName = formValues.lastName;

    getEntityGroupByName("Water Metering", "CUSTOMER").subscribe(
      function (entityGroup) {
        customerService.saveCustomer(vm.customer, entityGroup.id.id).subscribe(
          (customer) => {
            vm.user.authority = 'CUSTOMER_USER';
            vm.user.customerId = customer.id;

            const sendActivationMail = (formValues.userActivationMethod === 'sendActivationMail');
            const dashboardsPageLink = widgetContext.pageLink(1, 0, 'Water Metering User Dashboard');

            widgetContext.rxjs.forkJoin([
              entityGroupService.getEntityGroupsByOwnerId(customer.id.entityType, customer.id.id, 'USER', {ignoreLoading: true}),
              dashboardService.getTenantDashboards(dashboardsPageLink)]).subscribe(
              (response) => {
                let groups = response[0];
                let dashboard = response[1].data[0];
                let usersGroup = groups.filter((group) => {
                  return group.name === 'Customer Users';
                })[0];

                vm.user.additionalInfo = {
                  defaultDashboardFullscreen: true,
                  defaultDashboardId: dashboard.id.id
                };

                userService.saveUser(vm.user, sendActivationMail, usersGroup.id.id).subscribe(
                  (user) => {
                    saveAttributes(customer.id).subscribe(
                      () => {
                        setPermissions(customer, usersGroup,
                          function () { // onSuccess

                            vm.loading = false;
                            widgetContext.updateAliases();

                            if (formValues.userActivationMethod === 'displayActivationLink') {
                              userService.getActivationLink(user.id.id).subscribe(
                                (activationLink) => {
                                  displayActivationLink(activationLink).subscribe(
                                    () => {
                                      vm.dialogRef.close(null);
                                    }
                                  );
                                }
                              );
                            } else {
                              vm.dialogRef.close(null);
                            }

                          },
                          function () { // onFailure
                            customerService.deleteCustomer(customer.id.id).subscribe(
                              () => {
                                vm.loading = false;
                              });
                          }
                        );
                      }
                    );
                  },
                  () => {
                    customerService.deleteCustomer(customer.id.id).subscribe(
                      () => {
                        vm.loading = false;
                      });
                  }
                );
              }
            );

          },
          () => {
            vm.loading = false;
          }
        );
      });
  };

  function setPermissions(customer, usersGroup, onSuccess, onFailure) {
    const tasks = [];

    const readOnlyUserRolePageLink = widgetContext.pageLink(1, 0, 'Water Metering Read Only');
    const waterMetersUserRolePageLink = widgetContext.pageLink(1, 0, 'Water Metering User');
    const dashboardGroupsPageLink = widgetContext.pageLink(1, 0, 'Water Metering Shared');

    tasks.push(entityGroupService.getEntityGroups(dashboardGroupsPageLink, 'DASHBOARD', false, {ignoreLoading: true}));
    tasks.push(roleService.getRoles(readOnlyUserRolePageLink, null, {ignoreLoading: true}));
    tasks.push(roleService.getRoles(waterMetersUserRolePageLink, null, {ignoreLoading: true}));

    widgetContext.rxjs.forkJoin(tasks).subscribe(
      (response) => {
        let dashboardGroup = response[0].data[0];
        let readOnlyUserRole = response[1].data[0];
        let waterMetersUserRole = response[2].data[0];

        let readOnlyUserGroupPermission = {
          entityGroupId: dashboardGroup.id,
          entityGroupType: dashboardGroup.type,
          role: {
            type: readOnlyUserRole.type,
            id: {id: readOnlyUserRole.id.id}
          },
          roleId: readOnlyUserRole.id,
          userGroupId: usersGroup.id,
          userGroupOwnerId: customer.id
        };

        let waterMetersUserGroupPermission = {
          entityGroupId: null,
          entityGroupOwnerId: customer.ownerId,
          entityGroupType: null,
          role: {
            type: waterMetersUserRole.type,
            id: {id: waterMetersUserRole.id.id}
          },
          roleId: waterMetersUserRole.id,
          userGroupId: usersGroup.id
        };

        const tasks2 = [];
        tasks2.push(roleService.saveGroupPermission(readOnlyUserGroupPermission));
        tasks2.push(roleService.saveGroupPermission(waterMetersUserGroupPermission));
        widgetContext.rxjs.forkJoin(tasks2).subscribe(
          () => {
            onSuccess();
          },
          () => {
            onFailure();
          }
        );

      },
      () => {
        onFailure();
      }
    );
  }

  function saveAttributes(entityId) {
    let attributesArray = [];
    for (let key in vm.attributes) {
      if (vm.attributes[key] !== null) {
        attributesArray.push({key: key, value: vm.attributes[key]});
      }
    }
    if (attributesArray.length > 0) {
      return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
    } else {
      return widgetContext.rxjs.of([]);
    }
  }

  function displayActivationLink(activationLink) {
    const template = '<form style="min-width: 400px;">\n' +
      '  <mat-toolbar color="primary">\n' +
      '    <h2 translate>user.activation-link</h2>\n' +
      '    <span class="flex-1"></span>\n' +
      '    <button mat-icon-button\n' +
      '            (click)="close()"\n' +
      '            type="button">\n' +
      '      <mat-icon class="material-icons">close</mat-icon>\n' +
      '    </button>\n' +
      '  </mat-toolbar>\n' +
      '  <mat-progress-bar color="warn" mode="indeterminate" *ngIf="isLoading$ | async">\n' +
      '  </mat-progress-bar>\n' +
      '  <div style="height: 4px;" *ngIf="!(isLoading$ | async)"></div>\n' +
      '  <div mat-dialog-content tb-toast toastTarget="activationLinkDialogContent">\n' +
      '    <div class="mat-content flex flex-col">\n' +
      '      <span [innerHTML]="\'user.activation-link-text\' | translate: {activationLink: activationLink}"></span>\n' +
      '      <div class="flex flex-row items-center justify-start">\n' +
      '        <pre class="tb-highlight flex-1"><code>{{ activationLink }}</code></pre>\n' +
      '        <button mat-icon-button\n' +
      '                color="primary"\n' +
      '                ngxClipboard\n' +
      '                cbContent="{{ activationLink }}"\n' +
      '                (cbOnSuccess)="onActivationLinkCopied()"\n' +
      '                matTooltip="{{ \'user.copy-activation-link\' | translate }}"\n' +
      '                matTooltipPosition="above">\n' +
      '          <mat-icon svgIcon="mdi:clipboard-arrow-left"></mat-icon>\n' +
      '        </button>\n' +
      '      </div>\n' +
      '    </div>\n' +
      '  </div>\n' +
      '  <div mat-dialog-actions class="flex items-center justify-end">\n' +
      '    <button mat-button color="primary"\n' +
      '            type="button"\n' +
      '            cdkFocusInitial\n' +
      '            [disabled]="(isLoading$ | async)"\n' +
      '            (click)="close()">\n' +
      '      {{ \'action.ok\' | translate }}\n' +
      '    </button>\n' +
      '  </div>\n' +
      '</form>';
    return customDialog.customDialog(template, ActivationLinkDialogController, {activationLink: activationLink});
  }

  function ActivationLinkDialogController(instance) {
    var vm = instance;

    vm.activationLink = instance.data.activationLink;

    vm.onActivationLinkCopied = onActivationLinkCopied;
    vm.close = close;

    function onActivationLinkCopied(){
      widgetContext.showSuccessToast(translate.instant('user.activation-link-copied-message'), 1000, 'bottom', 'left', 'activationLinkDialogContent');
    }

    function close() {
      vm.dialogRef.close(null);
    }
  }
}

