click.customFunction:
var stateIndex = widgetContext.stateController
        .getStateIndex();
while (stateIndex > 0) {
    stateIndex -= 1;
    backToPrevState(stateIndex);
}

openDashboardState('default');

function backToPrevState(stateIndex) {
    widgetContext.stateController.navigatePrevState(
        stateIndex);
}

function openDashboardState(statedId) {
    var currentState = widgetContext.stateController
        .getStateId();
    if (currentState !== statedId) {
        var params = {};
        widgetContext.stateController.updateState(statedId,
            params, false);
    }
}

