var alarmsInfo = '';

if (dailyConsumptionThresholdExceeded) {
    alarmsInfo += '<br/><span>Daily consumption threshold exceeded.</span>';
} 
if (weeklyConsumptionThresholdExceeded) {
    alarmsInfo += '<br/><span>Weekly consumption threshold exceeded.</span>';
}
if (lowTemperature) {
    alarmsInfo += '<br/><span>Low temperature.</span>';
}

var label = generalInfo + telemetryInfo;

if (alarmsInfo) {
    label += '<div style="margin-top: 8px;"><span>Alarms:</span>' + alarmsInfo + '</div>';
}

return label;

