   border: 1px solid #E0E0E0;
   box-sizing: border-box;
}

.card .content {
   padding: 22px 30px;
   display: flex;
   flex-direction: column;
   align-items: center;
   justify-content: center;
   height: 100%;
   box-sizing: border-box;
}

.card .value {
    font-size: 1.5em;
    font-weight: bold;
    line-height: 1.5;
    color: #333333;
    text-align: center;
}

.card .description {
    font-size: 1em;
    font-weight: normal;
    line-height: 1.5;
    color: #333333;
    text-align: center;
}

@media (min-width: 960px) and (max-width: 1200px) {
    .card .value {
        font-size: 1.2em;
        line-height: 1;
    }

    .card .description {
        font-size: 0.8em;
        line-height: 1;
    }
}
