descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "SchedulerEventsSettings",
        "properties": {
            "title": {
                "title": "Scheduler events table title",
                "type": "string",
                "default": ""
            },
            "displayCreatedTime": {
                "title": "Display created time column",
                "type": "boolean",
                "default": true
