                "default": true
            },
            "displayPagination": {
                "title": "Display pagination",
                "type": "boolean",
                "default": true
            },
            "defaultPageSize": {
                "title": "Default page size",
                "type": "number",
                "default": 10
            },
            "defaultSortOrder": {
                "title": "Default sort order",
                "type": "string",
                "default": "name"
            },
            "enabledViews": {
                "title": "Enabled scheduler events views",
                "type": "string",
                "default": "both"
            },
            "forceDefaultEventType": {
                "title": "Force default event type",
                "type": "string",
                "default": ""
            },
            "customEventTypes": {
                "title": "Custom event types",
                "type": "array",
                "items": {
                    "title": "Event type",
                    "type": "object",
                    "required": ["name", "value", "template"],
                    "properties": {
                        "name": {
                            "title": "Display name",
                            "type": "string"
                        },
                        "value": {
                            "title": "Type name",
                            "type": "string"
                        },
                        "originator": {
                            "title": "Display originator entity select",
                            "type": "boolean"
                        },
                        "msgType": {
                            "title": "Display message type select",
                            "type": "boolean"
                        },
                        "metadata": {
                            "title": "Display message metadata table",
                            "type": "boolean"
                        },
                        "template": {
                            "title": "Configuration HTML template",
                            "type": "string"
                        }
                    }
                }
            }
        },
        "required": []
    },
    "form": [
        "title",
        "displayCreatedTime",
        "displayType",
        "displayCustomer",
        "displayPagination",
        "defaultPageSize",
        "defaultSortOrder",
        {
            "key": "enabledViews",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "both",
                    "label": "Both"
                },
                {
                    "value": "list",
                    "label": "List View Only"
                },
                {
                    "value": "calendar",
                    "label": "Calendar View Only"
                }
            ]
        },
        "forceDefaultEventType",
        {
            "key": "customEventTypes",
            "items": [
                "customEventTypes[].name",
                "customEventTypes[].value",
                "customEventTypes[].originator",
                "customEventTypes[].msgType",
                "customEventTypes[].metadata",
                {
                    "key": "customEventTypes[].template",
                    "type": "html"
                }
            ]
        }
    ]
}

